# Folders where the files moves
SortedFolder = {
		"_EXE_FILES" : "Program_Files",
		"_DOC_FILES" : "Document_Files",
		"_DATA_FILES" : "Data_Files",
		"_AUDIO_FILES" : "Audio_Files",
		"_VIDEO_FILES" : "Video_Files",
		"_SHEET_FILES" : "Sheet_Files",
		"_ZIPPED_FILES" : "Compressed_Files",
		"_IMAGE_FILES" : "Image_Files",
		"_TORRENT_FILES": "Torrent_Files",
		"_OTHER_FILES" : "Other_Files"
	}
#To Validate The sort
_Validate = False

if len(sys.argv) == 0:
	# Changing the directory to Download Folder
	os.chdir(expanduser("~")+"\\Downloads\\")
	print("[+] Path Changed To User's Download Folder \"{}\"".format(os.getcwd()))
else:
	# Customized folder
	os.chdir(sys.argv[1].replace('\\', '\\\\'))
	print("[+] Path Changed To Folder \"{}\"".format(os.getcwd()))

All_Files = [ file for file in os.listdir() if path.isfile(file) ]

# Checking if anything is there to move or not
if len(All_Files) == 0:
	print("[+] Nothing to Move")
	exit(0)
	
# Create folders if it's not present
for folder_name in SortedFolder.keys():
	if not path.exists(SortedFolder[folder_name]):
		os.mkdir(SortedFolder[folder_name])

# Find the file extention and make it same as the listed in FileExtList
for File in All_Files:
	File_ext = path.splitext(File)[1].upper()

	# Check the file extention there in the dictionary FileExtList if yes then move to folder 
	for ExtType in 	FileExtList.keys():
		if File_ext in FileExtList[ExtType]:
			shutil.move(File, SortedFolder[ExtType]+"\\"+File)
			print("[+] Moving to {} ".format(SortedFolder[ExtType]+"\\"+File))
			_Validate = True
	
	# Files which is not there in the list add to others
	if not _Validate:
		# ## shutil.move(File, SortedFolder["_OTHER_FILES"]+"\\"+File)
		print("[+] Moving to {} ".format(SortedFolder["_OTHER_FILES"]+"\\"+File))
